        internal static void Solve1()
        {
            char[] delimiters = new[] { ' ', '\n', '\r' };
            string[] strings = input.Split(delimiters, StringSplitOptions.RemoveEmptyEntries);
            var difference = 0;
            var listA = new List<int>();
            var listB = new List<int>();


            if (listA.Count != listB.Count)
            {
                Console.WriteLine("Error, lists not the same size.");
                return;
            }
            var sortedA = listA.OrderBy(x => x).ToArray();
            var sortedB = listB.OrderBy(x => x).ToArray();

            for (int i = 0; i < sortedA.Count(); i++)
            {
                difference += Math.Abs(sortedA[i] - sortedB[i]);
            }
            Console.WriteLine(difference);
        }
        internal static void Solve2()
        {
            char[] delimiters = new[] { ' ', '\n', '\r' };
            string[] strings = input.Split(delimiters, StringSplitOptions.RemoveEmptyEntries);

            var listA = new List<int>();
            var hashB = new Dictionary<int, int>();

            for (int i = 0; i < strings.Length; i += 2)
            {
                listA.Add(int.Parse(strings[i]));
                if (hashB.ContainsKey(int.Parse(strings[i+1])))
                {
                    hashB[int.Parse(strings[i + 1])]++;
                }
                else
                {
                    hashB.Add(int.Parse(strings[i + 1]), 1);
                }
            }
            var multiplier = 0;
            for (int i = 0; i < listA.Count(); i++)
            {
                if (hashB.ContainsKey(listA[i]))
                {
                    multiplier += listA[i] * hashB[listA[i]];
                }
            }
            Console.WriteLine(multiplier);
        }

